<script>export default {  name: 'HeaderSection'}</script><script setup>import Logo from "../Logo/";import Menu from "./HeaderSectionMenu";import BurgerMenu from "../ui/BurgerMenu";import BurgerButton from "../ui/buttons/BurgerButton";const router = useRouter();const isOpenBurgerMenu = ref(false);const toggleBurgerMenu = force => {  isOpenBurgerMenu.value = force ?? !isOpenBurgerMenu.value;};router.beforeEach(() => {  toggleBurgerMenu(false);})</script><template>  <header class="header">    <div class="header__inner">      <div class="content-container header__content">        <Logo/>        <Menu/>        <BurgerButton            :opened="isOpenBurgerMenu"            class="header__burger-btn"            @click="toggleBurgerMenu()"        />      </div>      <BurgerMenu          v-if="isOpenBurgerMenu"          class="header__burger-menu-wrap"          @close="() => { if (isOpenBurgerMenu) { toggleBurgerMenu(false) }}"      />    </div>  </header></template><style src="./style.scss" lang="scss" scoped/>