<script>export default {  name: 'OurPetsSection'}</script><script setup>import PetCard from "../PetCard";import SwiperControls from "../SwiperControls";import {pets} from '../../api/pets'const swiperOptions = {  breakpoints: {    1400: {      slidesPerView: 3,    },    1200: {      slidesPerView: 3,    },    992: {      slidesPerView: 2,    },    768: {      slidesPerView: 2,    },    576: {      slidesPerView: 1,    },    375: {      slidesPerView: 1,    },  },  autoplay: {    delay: 2000,    disableOnInteraction: true,  }}</script><template>  <section class="our-pets">    <div class="our-pets-inner content-container">      <h3 class="title-h3 title-h3-dart-text our-pets-title">Our friends who are looking for a house</h3>      <Swiper          :slides-per-view="3"          :loop="true"          :space-between="10"          :autoplay="swiperOptions.autoplay"          :breakpoints="swiperOptions.breakpoints"      >        <SwiperSlide v-for="pet in pets" :key="pet.id">          <div class="pet-lists">            <PetCard :pets="pet"/>          </div>        </SwiperSlide>        <SwiperControls/>      </Swiper>      <NuxtLink to="/our-pets">        <button class="btn btn-background our-pets-button">Get to know the rest</button>      </NuxtLink>    </div>  </section></template><style src="./style.scss" lang="scss" scoped/>