<script>export default {  name: 'PetCard'}</script><script setup>import BaseModal from "../BaseModal";import PetDescriptionInfo from "../PetDescriptionInfo";const props = defineProps({  pets: {    type: [Object, String],    default: null,  }})const showModal = ref(false);const toggleModal = force => {  showModal.value = force ?? !showModal.value;};const closeUserModal = () => {  toggleModal(false);};</script><template>  <section class="pet-card">    <div class="pet-card-inner">      <img :src="`_nuxt/assets/images/${props.pets.image}.png`" :alt="`${props.pets.alt}`" class="pet-card-img">      <div class="pet-card-name">{{ props.pets.name }}</div>      <button class="btn btn-transparent" @click="toggleModal">Learn more</button>    </div>  </section>  <Teleport to="body">    <BaseModal        v-show="showModal"        @close="closeUserModal"    >      <PetDescriptionInfo          :name="props.pets.name"          :image="props.pets.image"          :alt="props.pets.alt"          :age="props.pets.age"          :breed="props.pets.breed"          :description="props.pets.description"          :diseases="props.pets.diseases"          :inoculations="props.pets.inoculations"          :parasites="props.pets.parasites"          :type="props.pets.type"      />    </BaseModal>  </Teleport></template><style src="./style.scss" lang="scss" scoped/>