<script>export default {  name: 'PetDescriptionInfo'}</script><script setup>const props = defineProps({  name: {    type: String,    default: '',  },  image: {    type: [Object, String],    default: null,  },  type: {    type: String,    default: '',  },  breed: {    type: String,    default: '',  },  age: {    type: String,    default: '',  },  inoculations: {    type: String,    default: '',  },  diseases: {    type: String,    default: '',  },  parasites: {    type: String,    default: '',  },  description: {    type: String,    default: '',  },  alt: {    type: String,    default: '',  }})</script><template>  <div class="modal-card">    <img :src="`_nuxt/assets/images/${props.image}.png`" :alt="props.alt" class="modal-card__img">    <div class="modal-card__info">      <h3 class="title-h3 title-h3-dart-text modal-card-title">{{ props.name }}</h3>      <div class="modal-card-type">        <h4 class="title-h4">{{ props.type }}</h4> - <h4 class="title-h4">{{ props.breed }}</h4>      </div>      <p class="modal-card__description">{{ props.description }}</p>      <ul class="modal-card__other">        <li class="modal-card__item">          Age:          <span>            {{ props.age }}          </span>        </li>        <li class="modal-card__item">          Inoculations:          <span>{{ props.inoculations }}</span>        </li>        <li class="modal-card__item">          Diseases:          <span>{{ props.diseases }}</span>        </li>        <li class="modal-card__item">          Parasites:          <span>{{ props.parasites }}</span>        </li>      </ul>    </div>  </div></template><style src="./style.scss" lang="scss" scoped/>