<script>export default {  name: 'SwiperButton'}</script><script setup>const props = defineProps({  tag: {    type: String,    default: 'button'  },  to: {    type: [String, Object],    default: '',  }})const emit = defineEmits(['slide'])const onClick = () => {  emit('slide')}</script><template>  <component      :is="props.tag"      :to="props.to"      @click="onClick"      class="swiper-button"  >    <slot/>  </component></template><style src="./style.scss" lang="scss" scoped />