<script>export default {  name: 'OurPets'}</script><script setup>import PetCard from "../components/PetCard";import {pets} from "../api/pets";import UIPagination from "../components/ui/UIPagination";definePageMeta({  layout: 'our-pets'})</script><template>  <section class="content-container content-container--sm-gap  our-pets-page">    <h3 class="title-h3 title-h3-dart-text our-pets-title">      Our friends who are looking for a house    </h3>    <div class="pets-grid">      <div v-for="pet in pets" :key="pet.id">        <PetCard :pets="pet"/>      </div>    </div>    <UIPagination/>  </section></template><style scoped lang="scss">.our-pets-page {  background-color: $cLight-L;}.pets-grid {  display: grid;  grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));  gap: 40px;  margin-bottom: calc-rem(45px);}.our-pets-title {  max-width: 30%;  margin: 0 auto;  padding-top: calc-rem(80px);  padding-bottom: calc-rem(45px);  text-align: center;}@media #{$mMd} {  .our-pets-title {    max-width: 43%;  }}@media #{$mSm} {  .our-pets-title {    max-width: 78%;  }}@media #{$mXs} {  .our-pets-title {    max-width: 100%;  }}</style>